import React from 'react'
import styles from './styles.module.css'
import { karavanlar } from './karavandata';
import { Animation, ScrollContainer, ScrollPage,Animator,batch,Sticky,Fade } from 'react-scroll-motion'

function App() {

  const Spin = (cycle) =>
  ({
    in: {
      style: {
        // `p` is number (0~1)
        // When just before this page appear, `p` will be 0
        // When this page filled your screen, `p` will be 1
        transform: (p) => `rotate(${p * 360 * cycle}deg)`,
      },
    },
    out: {
      style: {
        // `p` is number (0~1)
        // When this page filled your screen, `p` will be 0
        // When just after this page disappear, `p` will be 1
        transform: (p) => `rotate(${p * 360 * cycle}deg)`,
      },
    },
  });

  return (
    <div className={styles.app}>
      <ScrollContainer>
        <ScrollPage>
    // Your custom animation also can be batched!
          <Animator animation={batch(Sticky(), Fade(), Spin(3))}>
            <h1 style={{ fontSize: 50 }}>Hello!!!</h1>
          </Animator>
        </ScrollPage>
        <ScrollPage>
          ...
        </ScrollPage>
      </ScrollContainer>
    </div>
  )
}

export default App
